import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import useWebSocket from "../../hooks/useWebSocket";
import "./Login.css";

const Login = ({ onLogin }) => {
  const { loginUser, messages } = useWebSocket();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // Sử dụng hook useNavigate

  useEffect(() => {
    messages.forEach((message) => {
      if (message.event === "LOGIN") {
        if (message.status === "success") {
          const storedUser = localStorage.getItem('currentUser');
          if (storedUser && storedUser === username) {
            // Người dùng đã đăng nhập, không thực hiện đăng nhập lại
            navigate(`/chat/${username}`); // Chuyển hướng đến trang chat
            return;
          }
          onLogin(username);
          navigate(`/chat/${username}`); // Chuyển hướng sau khi đăng nhập thành công
          localStorage.setItem('currentUser', username); // Lưu thông tin người dùng vào local storage
        } else {
          alert("Đăng nhập thất bại. Vui lòng thử lại.");
        }
      }
    });
  }, [messages, username, onLogin, navigate]);  

  const handleLogin = (e) => {
    e.preventDefault();
    if (username.trim() && password.trim()) {
      loginUser(username, password);
    } else {
      alert("Vui lòng nhập đầy đủ thông tin.");
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin}>
        <h2>Login</h2>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
        <p>
          Chưa có tài khoản? <Link to="/register">Register</Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
