import React, { useState } from 'react';
import './Login.css';
import useWebSocket from '../../hooks/useWebSocket'; // Đường dẫn đến hook

const Login = ({ onLogin }) => {
  const { sendMessage } = useWebSocket(); // Sử dụng hook để gửi tin nhắn
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    if (username.trim() && password.trim()) {
      sendMessage(JSON.stringify({ type: 'login', username, password }));
      onLogin(username); // Gọi hàm onLogin để đánh dấu đã đăng nhập thành công
    } else {
      alert('Vui lòng nhập đầy đủ thông tin.');
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin}>
        <h2>Đăng nhập</h2>
        <div className="form-group">
          <label htmlFor="username">Tên đăng nhập</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Mật khẩu</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Đăng nhập</button>
      </form>
    </div>
  );
};

export default Login;
