import React, { useState } from "react";
import Message from "./Message";
import MessageInput from "./MessageInput";
import UserInitializer from "./UserInitializer";

function ChatContainer({ messages, sendMessage }) {
  // Tạo một mảng để lưu trữ danh sách người dùng tham gia cuộc trò chuyện
  // const users = [...new Set(messages.map((message) => message.sender))];
  const [selectedUser, setSelectedUser] = useState(null);

  const handleUserSelect = (userId) => {
    // Xử lý thông tin về người dùng đã chọn
    setSelectedUser(userId);
  };

  let currentUser = "";
  if (selectedUser) {
    // Lặp qua danh sách tin nhắn để tìm tên người dùng hiện tại
    for (let i = 0; i < messages.length; i++) {
      if (messages[i].sender === selectedUser) {
        currentUser = messages[i].sender;
        break;
      }
    }
  }

  return (
    <div className="chat-container">
      <div className="show-chat">
        <div className="user-info">
          <UserInitializer onUserSelect={handleUserSelect} />
        </div>
        <div className="chat-messages">
          {/* chat messages sẽ hiển thị tên user ở trên cùng */}
          <div className="current-user">{currentUser}</div>
          {messages.map((message, index) => (
            <Message key={index} text={message.text} sender={message.sender} />
          ))}
        </div>
      </div>
      <MessageInput sendMessage={sendMessage} selectedUser={selectedUser}/>
    </div>
  );
}

export default ChatContainer;
