import React, { useState } from 'react';
import './Register.css';
import useWebSocket from '../../hooks/useWebSocket'; // Đường dẫn đến hook

const Register = ({ onRegister }) => {
  const { sendMessage } = useWebSocket(); // Sử dụng hook để gửi tin nhắn

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = (e) => {
    e.preventDefault();
    if (username.trim() && password.trim()) {
      // Gửi tin nhắn API với thông tin đăng ký
      sendMessage(JSON.stringify({ type: 'register', username, password }));
    } else {
      alert('Vui lòng nhập đầy đủ thông tin.');
    }
  };

  return (
    <div className="register-container">
      <form onSubmit={handleRegister}>
        <h2>Đăng ký</h2>
        <div className="form-group">
          <label htmlFor="username">Tên đăng nhập</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Mật khẩu</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Đăng ký</button>
      </form>
    </div>
  );
};

export default Register;
