import React, { useState, useEffect } from 'react';
import Message from './Message';
import MessageInput from './MessageInput';
import useWebSocket from '../hooks/useWebSocket';

function ChatContainer() {
  const { messages, sendMessage } = useWebSocket();
  const [selectedUser, setSelectedUser] = useState(null);
  const [currentUserMessages, setCurrentUserMessages] = useState([]);

  useEffect(() => {
    if (selectedUser) {
      const filteredMessages = messages.filter(
        (message) => message.data.data.sender === selectedUser || message.data.data.recipient === selectedUser
      );
      setCurrentUserMessages(filteredMessages);
    }
  }, [selectedUser, messages]);

  return (
    <div className="chat-container">
      <div className="show-chat">
        <div className="user-info">
          {/* User selection can be implemented here */}
        </div>
        <div className="chat-messages">
          {currentUserMessages.map((message, index) => (
            <Message key={index} text={message.data.data.mes} sender={message.data.data.sender} />
          ))}
        </div>
      </div>
      <MessageInput sendMessage={sendMessage} selectedUser={selectedUser} />
    </div>
  );
}

export default ChatContainer;
