import React, { useState } from 'react';
import './App.css';
import ChatContainer from './component/ChatContainer';
import Login from '../modules/Login/Login';
import Register from '../modules/Register/Register'; 
import useWebSocket from './hooks/useWebSocket';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false); // Thêm trạng thái đăng ký
  const [username, setUsername] = useState('');
  const { messages, sendMessage, clientUUID } = useWebSocket("ws://140.238.54.136:8080/chat/chat");

  const handleLogin = (username, password) => {
    // Logic kiểm tra đăng nhập
    // Ví dụ: gọi API để xác thực người dùng
    // Nếu đăng nhập thành công:
    setUsername(username);
    setIsLoggedIn(true);
  };

  const handleRegister = (username, password) => {
    // Logic để đăng ký tài khoản
    // Ví dụ: gọi API để tạo tài khoản mới
    // Sau khi đăng ký thành công, bạn có thể tự động đăng nhập hoặc yêu cầu người dùng đăng nhập lại
    setUsername(username);
    setIsLoggedIn(true);
  };

  if (!isLoggedIn && !isRegistering) {
    return <Login onLogin={handleLogin} onRegister={() => setIsRegistering(true)} />;
  } else if (!isLoggedIn && isRegistering) {
    return <Register onRegister={handleRegister} />;
  }

  return (
    <div className="App">
      <ChatContainer messages={messages} sendMessage={sendMessage} username={username} />
    </div>
  );
}

export default App;
