import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [messagesLeft, setMessagesLeft] = useState([]); // Tin nhắn của người dùng bên trái
  const [messagesRight, setMessagesRight] = useState([]); // Tin nhắn của người dùng bên phải
  const [messageInput, setMessageInput] = useState('');
  const [socket, setSocket] = useState(null); // State to hold WebSocket instance

  useEffect(() => {
    const newSocket = new WebSocket('ws://localhost:3000');
    setSocket(newSocket);

    newSocket.onopen = () => {
      console.log('WebSocket connection established.');
    };

    newSocket.onmessage = (event) => {
      const receivedMessage = JSON.parse(event.data);
      // Kiểm tra tin nhắn gửi từ ai và thêm vào phần tương ứng
      if (receivedMessage.sender === 'left') {
        setMessagesLeft(prevMessages => [...prevMessages, receivedMessage]);
      } else if (receivedMessage.sender === 'right') {
        setMessagesRight(prevMessages => [...prevMessages, receivedMessage]);
      }
    };

    return () => {
      newSocket.close();
    };
  }, []);

  const sendMessage = (sender) => {
    if (messageInput.trim() !== '' && socket && socket.readyState === WebSocket.OPEN) {
      const message = {
        text: messageInput,
        timestamp: new Date().toISOString(),
        sender: sender // Gửi thông tin về người gửi
      };
      socket.send(JSON.stringify(message));
      setMessageInput('');
    } else {
      console.error('WebSocket connection is not established yet or is not in OPEN state.');
    }
  };

  return (
    <div className="App">
      <div className="chat-container">
        <div className="chat-left">
          <div className="chat-messages">
            {messagesLeft.map((message, index) => (
              <div key={index} className="message">
                {message.text}
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message...."
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
            />
            <button onClick={() => sendMessage('left')}>Send</button>
          </div>
        </div>
        <div className="chat-right">
          <div className="chat-messages">
            {messagesRight.map((message, index) => (
              <div key={index} className="message">
                {message.text}
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message...."
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
            />
            <button onClick={() => sendMessage('right')}>Send</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
