import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');
  const [socket, setSocket] = useState(null); // State to hold WebSocket instance

  const [clientId, setClientId] = useState('');

  function generateClientId() {
    return Math.random().toString(36).substr(2, 9);
  }  

  useEffect(() => {
    const newSocket = new WebSocket('ws://localhost:3000');
    setSocket(newSocket);

    newSocket.onopen = () => {
      console.log('WebSocket connection established.');
      // Gửi mã định danh của máy khách đến máy chủ
      const clientId = generateClientId();
      setClientId(clientId);
      newSocket.send(clientId);
    };

    // Xử lý tin nhắn nhận được từ máy chủ
    newSocket.onmessage = (event) => {
      const receivedMessage = JSON.parse(event.data);
      setMessages(prevMessages => [...prevMessages, receivedMessage]);
    };

    return () => {
      newSocket.close();
    };
  }, []);


  const sendMessage = () => {
    if (messageInput.trim() !== '' && socket && socket.readyState === WebSocket.OPEN) {
      const message = {
        text: messageInput,
        timestamp: new Date().toISOString(),
        sender: 'me', // Xác định tin nhắn được gửi từ bản thân
      };
      socket.send(JSON.stringify(message));
      setMessageInput('');
      // Hiển thị tin nhắn đã gửi ngay sau khi gửi
      setMessages(prevMessages => [...prevMessages, message]);
    } else {
      console.error('WebSocket connection is not established yet or is not in OPEN state.');
    }
  };

  return (
    <div className="App">
      <div className="chat-container">
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              {message.text}
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            placeholder="Type your message...."
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default App;
