import React, { useState } from 'react';
import './Login.css';
import useWebSocket from '../../hooks/useWebSocket';
import Register from '../Register/Register';

const Login = ({ onLogin }) => {
  const { sendMessage, messages } = useWebSocket(); // Sử dụng hook để gửi tin nhắn
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Lắng nghe phản hồi từ server để kiểm tra người dùng
  React.useEffect(() => {
    messages.forEach((message) => {
      if (message.type === 'check_user') {
        if (message.exists) {
          onLogin(username, password);
        } else {
          alert('Tài khoản không tồn tại. Vui lòng đăng ký trước khi đăng nhập.');
        }
      }
    });
  }, [messages, username, password, onLogin]);

  const handleLogin = (e) => {
    e.preventDefault();
    if (username.trim() && password.trim()) {
      // Gửi yêu cầu kiểm tra người dùng
      sendMessage(JSON.stringify({ action: 'onchat', data: { event: 'CHECK_USER', data: { user: username } } }));
    } else {
      alert('Vui lòng nhập đầy đủ thông tin.');
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin}>
        <h2>Đăng nhập</h2>
        <div className="form-group">
          <label htmlFor="username">Tên đăng nhập</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Mật khẩu</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {/* <navigator><Register/></navigator> */}
        <button type="submit">Đăng nhập</button>
      </form>
    </div>
  );
};

export default Login;
