import { useState, useEffect } from 'react';

const useWebSocket = () => {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);
  const [clientUUID, setClientUUID] = useState(null);
  const url = "ws://140.238.54.136:8080/chat/chat";

  useEffect(() => {
    const newSocket = new WebSocket(url);
    setSocket(newSocket);

    newSocket.onopen = () => {
      console.log('WebSocket kết nối thành công.');
    };

    newSocket.onmessage = (event) => {
      try {
        const receivedMessage = JSON.parse(event.data);
        if (receivedMessage.event === 'RE_LOGIN') {
          setClientUUID(receivedMessage.data.RE_LOGIN_CODE); // Lưu RE_LOGIN_CODE của client
        } else {
          // Xử lý tin nhắn khác nếu cần
        }
        setMessages((prevMessages) => [...prevMessages, receivedMessage]);
      } catch (error) {
        console.error('Lỗi phân tích tin nhắn từ server:', error);
      }
    };

    newSocket.onclose = () => {
      console.log('WebSocket đã đóng.');
    };

    return () => {
      if (newSocket && newSocket.readyState === WebSocket.OPEN) {
        newSocket.close();
      }
    };
  }, []);

  const sendMessage = (message) => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.send(message);
    } else {
      console.error('WebSocket chưa được thiết lập hoặc chưa ở trạng thái mở.');
    }
  };

  return { messages, sendMessage, clientUUID };
};

export default useWebSocket;
