import React, { useState, useEffect } from "react";
import Message from "./Message";
import MessageInput from "./MessageInput";
import UserInitializer from "./UserInitializer";

function ChatContainer({ messages, sendMessage }) {
  const [selectedUser, setSelectedUser] = useState(null);
  const [currentUserMessages, setCurrentUserMessages] = useState([]);
  const [currentUser, setCurrentUser] = useState("");

  const handleUserSelect = (userId) => {
    setSelectedUser(userId);
  };

  useEffect(() => {
    // Nếu đã chọn người dùng và tồn tại tin nhắn từ người đó
    if (selectedUser) {
      const filteredMessages = messages.filter(
        (message) => message.sender === selectedUser || message.recipient === selectedUser
      );
      setCurrentUserMessages(filteredMessages);
      const foundUser = filteredMessages.find((message) => message.sender === selectedUser);
      setCurrentUser(foundUser ? foundUser.sender : "");
    }
  }, [selectedUser, messages]);

  return (
    <div className="chat-container">
      <div className="show-chat">
        <div className="user-info">
          <UserInitializer onUserSelect={handleUserSelect} />
        </div>
        <div className="chat-messages">
          {/* Hiển thị tên người dùng hiện tại */}
          <div className="current-user">{currentUser}</div>
          {currentUserMessages.map((message, index) => (
            <Message key={index} text={message.text} sender={message.sender} />
          ))}
        </div>
      </div>
      <MessageInput sendMessage={sendMessage} selectedUser={selectedUser} />
    </div>
  );
}

export default ChatContainer;
