import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import useWebSocket from '../../hooks/useWebSocket';
import './Login.css';

const Login = ({ onLogin }) => {
  const { sendMessage, messages } = useWebSocket(); // Sử dụng hook để gửi tin nhắn
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    messages.forEach((message) => {
      if (message.event === 'CHECK_USER') {
        if (message.data.exists) {
          onLogin(username);
        } else {
          alert('Tài khoản không tồn tại. Vui lòng đăng ký trước khi đăng nhập.');
        }
      }
    });
  }, [messages, username, onLogin]);

  const handleLogin = (e) => {
    e.preventDefault();
    if (username.trim() && password.trim()) {
      // Gửi yêu cầu kiểm tra người dùng
      sendMessage(JSON.stringify({ 
        action: 'onchat', 
        data: { event: 'CHECK_USER', data: { user: username } } 
      }));
    } else {
      alert('Vui lòng nhập đầy đủ thông tin.');
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin}>
        <h2>Đăng nhập</h2>
        <div className="form-group">
          <label htmlFor="username">Tên đăng nhập</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Mật khẩu</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Đăng nhập</button>
        <p>Chưa có tài khoản? <Link to="/register">Đăng ký ngay</Link></p>
      </form>
    </div>
  );
};

export default Login;
