import { useEffect, useRef, useState } from "react";

const useWebSocket = (currentUser) => {
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState(() => {
    const savedUsers = localStorage.getItem(`users_${currentUser}`);
    return savedUsers ? JSON.parse(savedUsers) : [];
  });
  const socketRef = useRef(null);
  const isConnectedRef = useRef(false);
  const isLoggedInRef = useRef(false);
  const url = "ws://140.238.54.136:8080/chat/chat";

  useEffect(() => {
    if (!socketRef.current) {
      const newSocket = new WebSocket(url);
      socketRef.current = newSocket;

      newSocket.onopen = () => {
        if (!isConnectedRef.current) {
          console.log("WebSocket connected successfully.");
          isConnectedRef.current = true;
          // Đăng nhập lại khi kết nối WebSocket mở lại
          loginUser(currentUser.username, currentUser.password);
        }
      };

      newSocket.onmessage = (event) => {
        console.log("Received message from server:", event.data);
        try {
          const receivedMessage = JSON.parse(event.data);
          console.log("Parsed message:", receivedMessage);
          setMessages((prevMessages) => [...prevMessages, receivedMessage]);

          if (
            receivedMessage.event === "LOGIN" &&
            receivedMessage.status === "success"
          ) {
            isLoggedInRef.current = true;
          }
        } catch (error) {
          console.error("Failed to parse message from the server:", error);
        }
      };

      newSocket.onclose = () => {
        console.log("WebSocket closed.");
        socketRef.current = null;
        isConnectedRef.current = false;
      };

      return () => {
        if (
          socketRef.current &&
          socketRef.current.readyState === WebSocket.OPEN
        ) {
          socketRef.current.close();
        }
      };
    }
  }, [currentUser]);

  useEffect(() => {
    localStorage.setItem(`users_${currentUser}`, JSON.stringify(users));
  }, [users, currentUser]);

  const sendMessage = (message) => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
      socketRef.current.send(message);
    } else {
      console.error("WebSocket is not open.");
      // Thử kết nối lại WebSocket nếu nó không mở
      if (
        !socketRef.current ||
        socketRef.current.readyState === WebSocket.CLOSED
      ) {
        const newSocket = new WebSocket(url);
        socketRef.current = newSocket;
        newSocket.onopen = () => {
          console.log("WebSocket reconnected successfully.");
          socketRef.current.send(message);
        };
      }
    }
  };

  const registerUser = (username, password) => {
    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: {
          event: "REGISTER",
          data: {
            user: username,
            pass: password,
          },
        },
      })
    );
  };

  const loginUser = (username, password) => {
    if (isLoggedInRef.current) {
      console.log("User is already logged in.");
      return;
    }

    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: {
          event: "LOGIN",
          data: {
            user: username,
            pass: password,
          },
        },
      })
    );
  };

  const sendChatMessage = (type, to, message) => {
    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: {
          event: "SEND_CHAT",
          data: {
            type: type,
            to: to,
            mes: message,
          },
        },
      })
    );
  };

  const addUser = (user) => {
    setUsers((prevUsers) => [...prevUsers, user]);
  };

  return {
    messages,
    sendMessage,
    registerUser,
    loginUser,
    sendChatMessage,
    users,
    addUser,
  };
};

export default useWebSocket;
