import { useEffect, useRef } from "react";

const useWebSocket = (currentUser) => {
  const messagesRef = useRef([]);
  const usersRef = useRef(() => {
    const savedUsers = localStorage.getItem(`users_${currentUser}`);
    return savedUsers ? JSON.parse(savedUsers) : [];
  });
  const socketRef = useRef(null);
  const isConnectedRef = useRef(false);
  const isLoggedInRef = useRef(flase);
  const url = "ws://140.238.54.136:8080/chat/chat";

  useEffect(() => {
    if (!socketRef.current) {
      const newSocket = new WebSocket(url);
      socketRef.current = newSocket;

      newSocket.onopen = () => {
        if (!isConnectedRef.current) {
          console.log("WebSocket connected successfully.");
          isConnectedRef.current = true;
        }
      };

      newSocket.onmessage = (event) => {
        console.log('Received message from server:', event.data);
        try {
          const receivedMessage = JSON.parse(event.data);
          console.log('Parsed message:', receivedMessage);
          messagesRef.current = [...messagesRef.current, receivedMessage];

          // Nếu nhận được tin nhắn thành công về việc đăng nhập, đặt isLoggedInRef thành true
          if (receivedMessage.event === "LOGIN" && receivedMessage.status === "success") {
            isLoggedInRef.current = true;
          }
        } catch (error) {
          console.error('Failed to parse message from the server:', error);
        }
      };

      newSocket.onclose = () => {
        console.log("WebSocket closed.");
        socketRef.current = null;
        isConnectedRef.current = false;
      };

      return () => {
        if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
          socketRef.current.close();
        }
      };
    }
  }, [currentUser]);

  useEffect(() => {
    localStorage.setItem(`users_${currentUser}`, JSON.stringify(usersRef.current()));
  }, [usersRef, currentUser]);

  const sendMessage = (message) => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
      socketRef.current.send(message);
    } else {
      console.error("WebSocket is not open.");
    }
  };

  const registerUser = (username, password) => {
    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: {
          event: "REGISTER",
          data: {
            user: username,
            pass: password
          }
        }
      })
    );
  };

  const loginUser = (username, password) => {
    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: {
          event: "LOGIN",
          data: {
            user: username,
            pass: password
          }
        }
      })
    );

    if (isLoggedInRef.current) {
      console.log("User is already logged in.");
      return;
    }
  };

  const sendChatMessage = (type, to, message) => {
    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: {
          event: "SEND_CHAT",
          data: {
            type: type,
            to: to,
            mes: message
          }
        }
      })
    );
  };

  const addUser = (user) => {
    usersRef.current = [...usersRef.current, user];
  };

  return { messages: messagesRef.current, sendMessage, registerUser, loginUser, sendChatMessage, users: usersRef.current(), addUser };
};

export default useWebSocket;
