import React, { useState, useEffect } from "react";
import Message from "./Message";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSmile } from '@fortawesome/free-solid-svg-icons';
import EmojiPicker from 'emoji-picker-react';
import "../App.css";
import useWebSocket from "../hooks/useWebSocket";

function ChatContainer({ currentUser, onLogout }) {
  const { messages, sendMessage, users, addUser } = useWebSocket(currentUser); 
  const [selectedUser, setSelectedUser] = useState(null);
  const [currentUserMessages, setCurrentUserMessages] = useState([]);
  const [newUser, setNewUser] = useState("");
  const [selectedUserName, setSelectedUserName] = useState("");
  const [messageInput, setMessageInput] = useState("");
  const [selectedEmoji, setSelectedEmoji] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  useEffect(() => {
    console.log("Selected user changed:", selectedUser);
    if (selectedUser) {
      const filteredMessages = messages.filter((message) => {
        const data = message.data?.data;
        return (
          data &&
          (data.sender === selectedUser || data.recipient === selectedUser)
        );
      });
      setCurrentUserMessages(filteredMessages);
    }
  }, [selectedUser, messages]);

  const handleAddUser = () => {
    if (newUser.trim()) {
      addUser(newUser);
      setNewUser("");
    }
  };

  const handleSelectUser = (user) => {
    setSelectedUser(user);
    setSelectedUserName(user);
  };

  const handleLogout = () => {
    onLogout();
  };

  const handleEmojiClick = (event, emojiObject) => {
    setSelectedEmoji(emojiObject.emoji);
  };

  const handleClickSend = () => {
    console.log("Selected user changed:", selectedUser);
    if (selectedUser) {
      const messageToSend = {
        action: 'onchat',
        data: {
          event: 'SEND_CHAT',
          data: {
            type: 'people',
            to: selectedUser,
            mes: messageInput + (selectedEmoji || '')
          }
        }
      };
      sendMessage(JSON.stringify(messageToSend));
      setMessageInput("");
      setSelectedEmoji(null);
    } else {
      alert('Please select a user to send the message.');
    }
  };  

  const toggleEmojiPicker = () => {
    setShowEmojiPicker(!showEmojiPicker);
  };

  return (
    <div className="chat-container">
      <div className="show-chat">
        <div className="user-info">
          <h2>Users</h2>
          <div className="user-list">
            {users.map((user, index) => (
              <div
                key={index}
                className={`user-item ${selectedUser === user ? "selected" : ""}`}
                onClick={() => handleSelectUser(user)}
              >
                {user}
              </div>
            ))}
          </div>
          <div className="add-user">
            <input
              type="text"
              value={newUser}
              onChange={(e) => setNewUser(e.target.value)}
              placeholder="Add new user"
            />
            <button onClick={handleAddUser}>Add User</button>
          </div>
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </div>
        <div className="chat-messages">
          <div className="chat-header">{selectedUserName}</div>
          {currentUserMessages.length > 0 ? (
            currentUserMessages.map((message, index) => (
              <Message
                key={index}
                text={message.data?.data?.mes}
                sender={message.data?.data?.sender === currentUser ? "me" : "you"}
              />
            ))
          ) : (
            <p>Please select a user to send the message.</p>
          )}
        </div>
      </div>
      <div className="chat-input">
        <div className="emoji-icon" onClick={toggleEmojiPicker}>
          <FontAwesomeIcon icon={faSmile} />
        </div>
        {showEmojiPicker && <EmojiPicker onEmojiClick={handleEmojiClick} />}
        <input
          type="text"
          placeholder="Type your message...."
          value={messageInput}
          onChange={(e) => setMessageInput(e.target.value)}
        />
        <button onClick={handleClickSend}>Send</button>
      </div>
    </div>
  );
}

export default ChatContainer;
