import React, { useState, useEffect } from "react";
import "./App.css";
import Login from "./component/Login";
import SignUp from "./component/SignUp";
import ChatContainer from "./component/ChatContainer";

function App() {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);
  const [clientUUID, setClientUUID] = useState(null);
  const [currentScreen, setCurrentScreen] = useState("login");

  useEffect(() => {
    const newSocket = new WebSocket("ws://140.238.54.136:8080/chat/chat");
    setSocket(newSocket);

    newSocket.onopen = () => {
      console.log("WebSocket kết nối thành công.");
    };

    socket.addEventListener("message", function (event) {
      console.log("Message from server:", event.data);
    });



    newSocket.onmessage = (event) => {
      try {
        const receivedMessage = JSON.parse(event.data);
        if (receivedMessage.type === 'uuid') {
          setClientUUID(receivedMessage.uuid); // Lưu UUID của client
        } else {
          // Xử lý tin nhắn khác nếu cần
        }
        setMessages((prevMessages) => [...prevMessages, receivedMessage]);
      } catch (error) {
        console.error("Lỗi phân tích tin nhắn từ server:", error);
      }
    };

    return () => {
      if (newSocket && newSocket.readyState === WebSocket.OPEN) {
        newSocket.close();
      }
    };
  }, []);

  const sendMessage = (text, selectedEmoji) => {
    if (
      (text.trim() !== "" || selectedEmoji !== null) &&
      socket &&
      socket.readyState === WebSocket.OPEN
    ) {
      const message = {
        text: text + (selectedEmoji ? ` ${selectedEmoji}` : ""),
        timestamp: new Date().toISOString(),
        sender: "me",
      };
      socket.send(JSON.stringify(message));
      setMessages((prevMessages) => [...prevMessages, message]);
    } else {
      console.error("WebSocket chưa được thiết lập hoặc chưa ở trạng thái mở.");
    }
  };

  const handleLoginSuccess = () => {
    setCurrentScreen("chat"); // Chuyển sang trạng thái chat khi đăng nhập thành công
  };

  const handleSignUpClick = () => {
    setCurrentScreen("signup"); // Chuyển sang trạng thái đăng ký khi nhấn vào nút đăng ký
  };

  return (
    <div className="App">
      <div className="App">
        {/* Hiển thị các component tùy thuộc vào trạng thái hiện tại */}
        {currentScreen === "login" && (
          <Login
            onLoginSuccess={handleLoginSuccess}
            onSignUpClick={handleSignUpClick}
          />
        )}
        {currentScreen === "signup" && <SignUp />}
        {currentScreen === "chat" && (
          <ChatContainer messages={messages} sendMessage={sendMessage} />
        )}
      </div>
    </div>
  );
}

export default App;
