import React, { useState } from 'react';

function UserInitializer({ onInitialize }) {
  // Sử dụng useState để lưu trữ danh sách người dùng
  const [users, setUsers] = useState([]);

  // Hàm để thêm một người dùng mới
  const addUser = (user) => {
    setUsers([...users, user]);
    // Gọi hàm onInitialize và truyền danh sách người dùng mới
    if (onInitialize) {
      onInitialize([...users, user]);
    }
  };

  return (
    <div className="user-initializer">
      <h2>User Initializer</h2>
      <button onClick={() => addUser('User 1')}>Add User 1</button>
      <button onClick={() => addUser('User 2')}>Add User 2</button>
      {/* Thêm các nút hoặc phần tử khác để khởi tạo người dùng */}
    </div>
  );
}

export default UserInitializer;
