import React, { useState, useEffect } from 'react';
import Message from './Message';
import MessageInput from './MessageInput';
import useWebSocket from '../hooks/useWebSocket';
import '../App.css';

function ChatContainer() {
  const { messages, sendMessage, users, addUser } = useWebSocket();
  const [selectedUser, setSelectedUser] = useState(null);
  const [currentUserMessages, setCurrentUserMessages] = useState([]);
  const [newUser, setNewUser] = useState('');
  const [selectedUserName, setSelectedUserName] = useState(''); // Thêm state để lưu trữ tên người dùng được chọn

  useEffect(() => {
    if (selectedUser) {
      const filteredMessages = messages.filter((message) => {
        const data = message.data?.data; 
        return data && (data.sender === selectedUser || data.recipient === selectedUser);
      });
      setCurrentUserMessages(filteredMessages);
    }
  }, [selectedUser, messages]);

  const handleAddUser = () => {
    if (newUser.trim()) {
      addUser(newUser);
      setNewUser('');
    }
  };

  const handleSelectUser = (user) => {
    setSelectedUser(user);
    setSelectedUserName(user); // Cập nhật tên người dùng được chọn
  };

  return (
    <div className="chat-container">
      <div className="show-chat">
        <div className="user-info">
          <h2>Users</h2>
          <div className="user-list">
            {users.map((user, index) => (
              <div
                key={index}
                className={`user-item ${selectedUser === user ? 'selected' : ''}`}
                onClick={() => handleSelectUser(user)}
              >
                {user}
              </div>
            ))}
          </div>
          <div className="add-user">
            <input
              type="text"
              value={newUser}
              onChange={(e) => setNewUser(e.target.value)}
              placeholder="Add new user"
            />
            <button onClick={handleAddUser}>Add User</button>
          </div>
        </div>
        <div className="chat-messages">
          <div className="chat-header">{selectedUserName}</div> {/* Hiển thị tên người dùng ở góc trên trái */}
          {currentUserMessages.length > 0 ? (
            currentUserMessages.map((message, index) => (
              <Message
                key={index}
                text={message.data.mes}
                sender={message.data.sender === selectedUser ? 'me' : 'you'} // Gán loại tin nhắn tương ứng
              />
            ))
          ) : (
            <p>Please select a user to send the message.</p>
          )}
        </div>
      </div>
      <MessageInput sendMessage={sendMessage} selectedUser={selectedUser} />
    </div>
  );
}

export default ChatContainer;
