import React, { useState, useEffect } from 'react';
import Message from './Message';
import MessageInput from './MessageInput';
import useWebSocket from '../hooks/useWebSocket';
import '../App.css';

function ChatContainer() {
  const { messages, sendMessage, users } = useWebSocket();
  const [selectedUser, setSelectedUser] = useState(null);
  const [currentUserMessages, setCurrentUserMessages] = useState([]);
  const [newUser, setNewUser] = useState('');

  useEffect(() => {
    if (selectedUser) {
      const filteredMessages = messages.filter(
        (message) =>
          message.data.data.sender === selectedUser ||
          message.data.data.recipient === selectedUser
      );
      setCurrentUserMessages(filteredMessages);
    }
  }, [selectedUser, messages]);

  const handleAddUser = () => {
    if (newUser.trim()) {
      users.push(newUser);  // Thêm người dùng mới vào danh sách
      setNewUser('');
    }
  };

  return (
    <div className="chat-container">
      <div className="show-chat">
        <div className="user-info">
          <h2>Users</h2>
          <ul>
            {users.map((user, index) => (
              <li
                key={index}
                className={selectedUser === user ? 'selected' : ''}
                onClick={() => setSelectedUser(user)}
              >
                {user}
              </li>
            ))}
          </ul>
          <div className="add-user">
            <input
              type="text"
              value={newUser}
              onChange={(e) => setNewUser(e.target.value)}
              placeholder="Add new user"
            />
            <button onClick={handleAddUser}>Add User</button>
          </div>
        </div>
        <div className="chat-messages">
          {currentUserMessages.length > 0 ? (
            currentUserMessages.map((message, index) => (
              <Message
                key={index}
                text={message.data.data.mes}
                sender={message.data.data.sender}
              />
            ))
          ) : (
            <p>Please select a user to send the message.</p>
          )}
        </div>
      </div>
      <MessageInput sendMessage={sendMessage} selectedUser={selectedUser} />
    </div>
  );
}

export default ChatContainer;
