import React, { useState, useEffect } from "react";
import "./App.css";
import EmojiPicker from 'emoji-picker-react';

function App() {
  const [messages, setMessages] = useState([]);
  const [messageInput, setMessageInput] = useState("");
  const [socket, setSocket] = useState(null); // State to hold WebSocket instance
  const [selectedEmoji, setSelectedEmoji] = useState(null);

  useEffect(() => {
    const newSocket = new WebSocket("ws://localhost:3000");
    setSocket(newSocket);

    newSocket.onopen = () => {
      console.log("WebSocket connection established.");
    };
    newSocket.onmessage = (event) => {
      const receivedMessage = JSON.parse(event.data);
      setMessages((prevMessages) => [...prevMessages, receivedMessage]);
    };    

    return () => {
      newSocket.close();
    };
  }, []);

  const handleEmojiClick = (event, emojiObject) => {
    setEmojiInput(emojiObject.emoji);
  };
  const sendMessage = () => {
    if (
      messageInput.trim() !== "" &&
      socket &&
      socket.readyState === WebSocket.OPEN
    ) {

      const message = {
        text: messageInput + (emojiInput ? ` ${emojiInput}` : ""),
        timestamp: new Date().toISOString(),
        sender: "me", // Xác định tin nhắn được gửi từ bản thân
      };
      socket.send(JSON.stringify(message));
      setMessageInput("");
      setEmojiInput(null);
      // Hiển thị tin nhắn đã gửi ngay sau khi gửi
      setMessages((prevMessages) => [...prevMessages, message]);
    } else {
      console.error(
        "WebSocket connection is not established yet or is not in OPEN state."
      );
    }
  };

  return (
    <div className="App">
      <div className="chat-container">
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender === 'me' ? 'me' : 'you'}`}>
              {message.text}
            </div>          
          ))}
        </div>
        <div className="chat-input">
          <div>
            <EmojiPicker onEmojiClick={handleEmojiClick} />
          </div>
          <input
            type="text"
            placeholder="Type your message...."
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default App;
