// ChatContainer.js
import React, { useState, useEffect } from "react";
import MessageInput from "./MessageInput";
import useWebSocket from "../hooks/useWebSocket";

function ChatContainer({ currentUser, onLogout }) {
  const { messages, sendMessage, users } = useWebSocket(currentUser);
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    console.log("Selected user changed:", selectedUser);
    if (selectedUser) {
      // Load messages for the selected user
    }
  }, [selectedUser]);

  const handleSelectUser = (user) => {
    setSelectedUser(user);
  };

  return (
    <div className="chat-container">
      <div className="user-list">
        <h2>Users</h2>
        {users.map((user, index) => (
          <div key={index} onClick={() => handleSelectUser(user)}>{user}</div>
        ))}
      </div>
      <MessageInput selectedUser={selectedUser} sendMessage={sendMessage} />
    </div>
  );
}

export default ChatContainer;
