import React, { useState, useEffect } from "react";
import "./App.css";
import ChatContainer from "./component/ChatContainer";

function App() {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);
  const [clientUUID, setClientUUID] = useState(null); 

  useEffect(() => {
    const newSocket = new WebSocket("ws://localhost:3000");
    setSocket(newSocket);

    newSocket.onopen = () => {
      console.log("WebSocket connection established.");
    };
    newSocket.onmessage = (event) => {
      const receivedMessage = JSON.parse(event.data);
      if (receivedMessage.type === 'uuid') {
        setClientUUID(receivedMessage.uuid); // Lưu UUID của client
      } else {
        // Xử lý tin nhắn khác nếu cần
      }
      setMessages((prevMessages) => [...prevMessages, receivedMessage]);
    };

    return () => {
      newSocket.close();
    };
  }, []);

  const sendMessage = (text, selectedEmoji) => {
    if (
      (text.trim() !== "" || selectedEmoji !== null) &&
      socket &&
      socket.readyState === WebSocket.OPEN
    ) {
      const message = {
        text: text + (selectedEmoji ? ` ${selectedEmoji}` : ""),
        timestamp: new Date().toISOString(),
        sender: "me",
      };
      socket.send(JSON.stringify(message));
      setMessages((prevMessages) => [...prevMessages, message]);
    } else {
      console.error(
        "WebSocket connection is not established yet or is not in OPEN state."
      );
    }
  };

  return (
    <div className="App">
      <ChatContainer messages={messages} sendMessage={sendMessage} />
    </div>
  );
}

export default App;
