import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSmile } from '@fortawesome/free-solid-svg-icons';
import EmojiPicker from 'emoji-picker-react';
import '../App.css';
import useWebSocket from "../hooks/useWebSocket"; 

function MessageInput({ selectedUser }) {
  const { sendMessage } = useWebSocket(selectedUser);
  const { loginUser, messages } = useWebSocket();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [messageInput, setMessageInput] = useState("");
  const [selectedEmoji, setSelectedEmoji] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  const handleEmojiClick = (event, emojiObject) => {
    setSelectedEmoji(emojiObject.emoji);
  };

  const handleClickSend = () => {
    console.log("Selected user changed:", selectedUser);
    if (selectedUser) {
      const message = JSON.stringify({
        action: 'onchat',
        data: {
          event: 'SEND_CHAT',
          data: {
            type: 'people',
            to: selectedUser,
            mes: messageInput + (selectedEmoji || '')
          }
        }
      });
      sendMessage(message);
      setMessageInput("");
      setSelectedEmoji(null);
    } else {
      alert('Please select a user to send the message.');
    }
  };

  const toggleEmojiPicker = () => {
    setShowEmojiPicker(!showEmojiPicker);
  };

  return (
    <div className="chat-input">
      <div className="emoji-icon" onClick={toggleEmojiPicker}>
        <FontAwesomeIcon icon={faSmile} />
      </div>
      {showEmojiPicker && <EmojiPicker onEmojiClick={handleEmojiClick} />}
      <input
        type="text"
        placeholder="Type your message...."
        value={messageInput}
        onChange={(e) => setMessageInput(e.target.value)}
      />
      <button onClick={handleClickSend}>Send</button>
    </div>
  );
}

export default MessageInput;
