import { useState, useEffect } from "react";

const useWebSocket = () => {
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);
  const url = "ws://140.238.54.136:8080/chat/chat";

  useEffect(() => {
    const newSocket = new WebSocket(url);
    setSocket(newSocket);

    newSocket.onopen = () => {
      console.log("WebSocket connected successfully.");
    };

    newSocket.onmessage = (event) => {
      try {
        const receivedMessage = JSON.parse(event.data);
        if (receivedMessage.event) {
          setMessages((prevMessages) => [...prevMessages, receivedMessage]);
        } else if (receivedMessage.status) {
          if (receivedMessage.status === 'error') {
            console.error('Error:', receivedMessage.mes);
          } else if (receivedMessage.status === 'success') {
            console.log('Success:', receivedMessage.data);
          }
        } else {
          console.error('Tin nhắn nhận được có cấu trúc không hợp lệ:', receivedMessage);
        }
      } catch (error) {
        console.error('không gửi được tin lên server:', error);
      }
    };

    newSocket.onclose = () => {
      console.log("WebSocket closed.");
    };

    return () => {
      if (newSocket && newSocket.readyState === WebSocket.OPEN) {
        newSocket.close();
      }
    };
  }, []);

  const sendMessage = (message) => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.send(message);
    } else {
      console.error("WebSocket is not open.");
    }
  };

  const registerUser = (username, password) => {
    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: { event: "REGISTER", data: { user: username, pass: password } },
      })
    );
  };

  const loginUser = (username, password) => {
    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: { event: "LOGIN", data: { user: username, pass: password } },
      })
    );
  };

  const sendChatMessage = (type, to, message) => {
    sendMessage(
      JSON.stringify({
        action: "onchat",
        data: { event: "SEND_CHAT", data: { type, to, mes: message } },
      })
    );
  };

  return { messages, sendMessage, registerUser, loginUser, sendChatMessage };
};

export default useWebSocket;
